	END IF;

	EXECUTE FORMAT('SELECT %s FROM %s WHERE %s = ' || quote_literal(bugs_code) || ';',sead_id_col,table_name,sead_bugs_col)
	INTO	recid;

	result = recid;
END;
$_$;


