	IF (tsrow.longdd IS NULL AND srec.longitude_dd IS NULL) IS FALSE THEN
		IF tsrow.longdd = srec.longitude_dd THEN
			 -- case where both are exactly equal
		ELSE
			-- Conflict BugsError
